import React, { useEffect, useState } from 'react';
import { 
  Box, 
  Typography, 
  CircularProgress,
  Button 
} from '@mui/material';
import { getSystemStatus, SystemStatus } from '../../services/atcService';
import { AirspaceStatus } from './AirspaceStatus';
import { RunwayStatus } from './RunwayStatus';
import { LoadingStatus } from './LoadingStatus';
import { Link } from 'react-router-dom';
import { MessageLog } from './MessageLog';

export const Dashboard: React.FC = () => {
  const [status, setStatus] = useState<SystemStatus | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchStatus = async () => {
    try {
      const data = await getSystemStatus();
      setStatus(data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch system status');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStatus();
    const interval = setInterval(fetchStatus, 5000); // Refresh every 5 seconds
    return () => clearInterval(interval);
  }, []);

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!status) return <Typography>No data available</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          Air Traffic Control Dashboard
        </Typography>
        <Button 
          component={Link}
          to="/create-plane"
          variant="contained"
          color="primary"
        >
          Create New Plane
        </Button>
      </Box>
      
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Box sx={{ flex: 1 }}>
            <AirspaceStatus 
              airspaces={status.airspaces} 
              airplanes={status.airplanes}  // Added this prop
            />
          </Box>
          <Box sx={{ flex: 1 }}>
            <RunwayStatus 
              runways={status.runways} 
              airplanes={status.airplanes}  // Added this prop
            />
          </Box>
        </Box>
        
        <LoadingStatus loading={status.loading} />
      </Box>
      <MessageLog />
    </Box>
  );
};
