import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Select, 
  MenuItem, 
  FormControl, 
  InputLabel, 
  Paper 
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { createPlane } from '../../services/atcService';

export const CreatePlaneForm: React.FC = () => {
  const [planeId, setPlaneId] = useState('');
  const [state, setState] = useState<'air' | 'ground'>('air');
  const [position, setPosition] = useState('outer_airspace');
  const [target, setTarget] = useState('runway_1');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      await createPlane(planeId, state, position, target);
      navigate('/');
    } catch (err) {
      setError('Failed to create plane. Please check your inputs.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3, maxWidth: 500, mx: 'auto' }}>
      <Typography variant="h5" gutterBottom>
        Create New Plane
      </Typography>
      
      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}
      
      <Box component="form" onSubmit={handleSubmit}>
        <TextField
          label="Plane ID"
          value={planeId}
          onChange={(e) => setPlaneId(e.target.value)}
          fullWidth
          required
          margin="normal"
        />
        
        <FormControl fullWidth margin="normal">
          <InputLabel>State</InputLabel>
          <Select
            value={state}
            onChange={(e) => {
              const newState = e.target.value as 'air' | 'ground';
              setState(newState);
              setPosition(newState === 'air' ? 'outer_airspace' : 'loading');
              setTarget(newState === 'air' ? 'runway_1' : 'runway_1');
            }}
            label="State"
            required
          >
            <MenuItem value="air">Air</MenuItem>
            <MenuItem value="ground">Ground</MenuItem>
          </Select>
        </FormControl>
        
        <TextField
          label="Position"
          value={position}
          disabled
          fullWidth
          margin="normal"
        />
        
        <FormControl fullWidth margin="normal">
          <InputLabel>Target</InputLabel>
          <Select
            value={target}
            onChange={(e) => setTarget(e.target.value)}
            label="Target"
            required
          >
            <MenuItem value="runway_1">Runway 1</MenuItem>
            <MenuItem value="runway_2">Runway 2</MenuItem>
          </Select>
        </FormControl>
        
        <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end' }}>
          <Button 
            type="submit" 
            variant="contained" 
            disabled={loading}
          >
            {loading ? 'Creating...' : 'Create Plane'}
          </Button>
        </Box>
      </Box>
    </Paper>
  );
};
